import R from 'ramda';

const getGuitarById = (state, id) => R.prop(id, state.guitars);

export const getGuitars = state => {
  const guitars = R.map(id => getGuitarById(state, id), state.guitarsPage.ids);
  return guitars;
};

export const getRenderedGuitarsLength = state => R.length(state.guitarsPage.ids);